custom.scss:
@font-face {
    font-family: 'AvenirBlack';
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirBlack.eot');
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirBlack.eot') format('embedded-opentype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirBlack.woff2') format('woff2'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirBlack.woff') format('woff'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirBlack.ttf') format('truetype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirBlack.svg#AvenirBlack') format('svg');
}
@font-face {  
    font-family: 'AvenirMedium';
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirMedium.eot');
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirMedium.eot') format('embedded-opentype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirMedium.woff2') format('woff2'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirMedium.woff') format('woff'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirMedium.ttf') format('truetype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirMedium.svg#AvenirMedium') format('svg');
}
@font-face { 
    font-family: 'AvenirHeavy';
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirHeavy.eot');
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirHeavy.eot') format('embedded-opentype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirHeavy.woff2') format('woff2'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirHeavy.woff') format('woff'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirHeavy.ttf') format('truetype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/AvenirHeavy.svg#AvenirHeavy') format('svg');
}

@font-face {
    font-family: 'Dead Stock';
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/DeadStock.eot');
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/DeadStock.eot?#iefix') format('embedded-opentype'),
        url('https://wds-sandbox14.mybigcommerce.com/content/font/DeadStock.woff2') format('woff2'),
        url('https://wds-sandbox14.mybigcommerce.com/content/font/DeadStock.woff') format('woff'),
        url('https://wds-sandbox14.mybigcommerce.com/content/font/DeadStock.ttf') format('truetype'),
        url('https://wds-sandbox14.mybigcommerce.com/content/font/DeadStock.svg#DeadStock') format('svg');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}

@font-face {
    font-family: 'brandon_grotesquebold'; 
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/brandon_grotesque_bold-webfont.eot');
    src: url('https://wds-sandbox14.mybigcommerce.com/content/font/brandon_grotesque_bold-webfont.eot?#iefix') format('embedded-opentype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/brandon_grotesque_bold-webfont.woff2') format('woff2'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/brandon_grotesque_bold-webfont.woff') format('woff'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/brandon_grotesque_bold-webfont.ttf') format('truetype'),
         url('https://wds-sandbox14.mybigcommerce.com/content/font/brandon_grotesque_bold-webfont.svg#brandon_grotesquebold') format('svg');
    font-weight: normal;
    font-style: normal;

}


$font5:'brandon_grotesquebold'; 
$font1:'AvenirMedium';
$font2: 'AvenirBlack';
$font3:'AvenirHeavy';
$font4:'Dead Stock';
$black:#000; 
$white:#fff; 
$blue:#03008C; 
$red:#CB4335;
$gray:#999999;

@mixin row {    
  float:left; width:100%;  
}
@mixin row_half {    
  float:left; width:50%;
}
@mixin overlap_text {   
  position:absolute;top:50%;transform: translateY(-50%);-webkit-transform: translateY(-50%);-moz-transform: translateY(-50%); -ms-transform: translateY(-50%);-o-transform: translateY(-50%);   text-align:center;margin:0 auto;
}
.skip-to-main-link{display: none;}
*{
  outline: none;
}
body [contenteditable=true]:focus, body [href]:focus, body [tabindex]:not([tabindex="-1"]):focus, body button:focus, body details:focus, body input:focus, body select:focus, body textarea:focus{
    outline: none !important;
}
a, a:focus{
color:$black;
}
a, a:focus, a:hover{   
  text-decoration:none;
}
a:hover {
color: $red;
}
 
@mixin add_translate {  
transform:translateX(-50%) translateY(-55%); -ms-transform: translateX(-50%) translateY(-55%); -webkit-transform: translateX(-50%) translateY(-55%); -moz-transform: translateX(-50%) translateY(-55%); -o-transform: translateX(-50%) translateY(-55%);
}
@mixin transition{  
  transition: all 0.4s ease;-webkit-transition: all 0.4s ease;-moz-transition: all 0.4s ease;-ms-transition: all 0.4s ease;-o-transition: all 0.4s ease;
}

@mixin stickytransition{
      -webkit-transition: all 0.5s ease;
    -moz-transition: all 0.5s ease;
    -o-transition: all 0.5s ease;
    transition: all 0.5s ease;
}
@mixin btn{ 
  background-color:$black; color:$white; line-height:normal;border-radius: 6px; outline:none; font-family:$font3; font-weight:normal;text-transform: capitalize;border:1px solid $black;font-size: 16px;letter-spacing: 0px;@include transition;
  &:hover{  
    background-color:$red; color:$white;border-color: $red;@include transition;
  }
} 
@mixin HP_heading{
  font-size: 34px;font-family: $font2;font-weight: normal;color: $black;line-height: 38px;text-transform: uppercase;display: inline-block;width: 100%;letter-spacing: 0px;text-align: center;
}

@mixin img_hover{
  transform: scale(1.1);@include transition;
  &:hover{
    transform: scale(1.1);@include transition;
  }
}
.button, .button:focus, .button:active, .swal2-container .swal2-confirm.button, .swal2-container .swal2-modal .swal2-cancel, .modal--alert .button-container .confirm
{ @include btn; @include transition;
}
.button:hover, .swal2-container .swal2-confirm.button:hover, .swal2-container .swal2-modal .swal2-cancel:hover, .modal--alert .button-container .confirm:hover
{  @include transition;
}
h1, h2, h3, h4, h5, h6{
  font-family: $font2;font-weight: normal;color:$black;
}
body
{
  font-family:$font1; color: $black;font-weight: normal;font-size: 16px;
}
.body{
    min-height: 550px;
}
.body, .main.full{
@include row;
}
.custom_default, .custom_category, .custom_product, .custom_brand, .custom_search{
	.body{
	  margin-top: 0;
	}
}
.row{
  @include row;
}
.footer{
    @include row;background: transparent;
}
.header{
	@include row;background:$white;border:0;display: block;position: fixed;top:0;
	.container{
		max-width: 100%;
	}
}
.header-logo{
    margin: 0;text-align: left;display: inline-block;
    .header-logo-image-unknown-size{
        max-width:197px;
        
    }
}
.header{
	padding: 26px 39px 26px 54px;
	.header-inner{
		display: flex;justify-content: space-between;flex-wrap: wrap;
	}
	.header-logo{
		width: 16%;float: left;
	}
	.navPages-container{
		width: 65%;text-align: center;float: left;padding: 13px 0 0 3px;
	}
	.navUser{
		width: 19%;text-align: right;padding: 9px 0 0;float: left;
	}
}
.header{
	.navPages-container{
		.navPages-list{
			.navPages-item{
				padding: 0 16.5px;position: relative;
				.navPages-action, .navUser-action{
					font-size: 18px;line-height: 25px;letter-spacing: 0.18px;color: $black;text-transform: capitalize;padding: 0;font-weight: normal;
					.icon{
						margin-left: 5px;display: none;
						svg{
							fill:$black;stroke: $black;margin: 0;
						}
					}
					svg{
						fill:none;stroke: none;
					}
					&.is-open{
						background: transparent;color: $red;
					}
					&:after{
						content: "";display: inline-block;position: absolute;left: 0;right: 0;text-align: center;margin: 0 auto;background: transparent;width: 19px;height: 3px;top: 33px;
						z-index: -1;
					}
					&:hover{
						color: $red;
						&:after{
						background: $red;
						}
					}
				}
				&.hdr_serach{
					padding: 0 0 0 32px;
					.navUser-action{
						svg{
							margin: -4px 0 0;
					    }
					    &:after{
					    	display: none;
					    }
					    &:hover{
					    	svg{
					    		#Path_59{
					    			fill:$red;
					    		}
					    	}
					    }
					}
				}

			}
		}
	}
}
.header{
	.navPages-container{
		.navPage-subMenu{
			padding: 0;min-width: 180px;border:1px solid #ccc;border-radius: 6px;padding: 15px 0 8px 0;
			left:0;background: $white;left: 50%;transform: translateX(-50%);
			.navPage-subMenu-list{
				.navPage-subMenu-item{
					width: 100%;text-align: center;
					.navPages-action{
						border:0;font-size: 16px;font-weight: normal;font-family: $font1;margin: 0 0 6px;width: auto;
						&:hover{
							color:$red;
						}
						&:after{
							display: none;
						}
					}
				}
			}
		}
	}
}

.dropdown--quickSearch{
	position: absolute;top:173px;left:auto;right:-320px;background:#FBFBFB;z-index: 4;
	padding:0 0 7px;width: 650px;border-bottom: 1px solid #707070;@include transition;display: block;opacity: 0;visibility: hidden;top:-5px !important;
	.container{
		max-width: 100%;padding: 0;
	}
	 .form{
      margin: 0;max-width: 100%;display: flex;
     
      .form-input{
      border:0;font-size: 18px;font-weight: normal;text-transform: none;font-family: $font1;
        height: 28px;line-height: 26px;border:0;width:100%;letter-spacing: -0.36px;color: $gray;border-radius: 0; padding: 10px 0px 0 0px;
         
      }
      .search_submit{
        height: 28px;width: 28px;position: absolute;right: 10px;top:0;border: 0;background:transparent;
        margin: 0;padding: 0;
        &:hover{
	    	svg{
	    		#Path_59{
	    			fill:$red;
	    		}
	    	} 
	    }
      }
      .form-field{
        margin: 0;width:480px;float: left;
        &.category_search_products{
	      	width: 95px;height: 34px;margin-right: 28px;
	      	.form-select{
	      		padding:0 28px 0 15px;font-size: 18px;color: $black;border-radius: 6px;background: #F2F2F2 url("https://wds-sandbox14.mybigcommerce.com/content/dropdown-arrow.svg") no-repeat 84% center;
	      		border-color: #F2F2F2;height: 34px;
	      	}
	      }
      }
    }
    &.is-open{
    	width: 650px !important;left: auto !important;right:0 !important;@include transition;opacity: 1;visibility: visible;background:#fff;
    }
}


.dropdown--quickSearch .form .form-input::-webkit-input-placeholder { /* Edge */
  color:#999;
}

.dropdown--quickSearch .form .form-input:-ms-input-placeholder { /* Internet Explorer 10-11 */
  color:#999;
}

.dropdown--quickSearch .form .form-input::placeholder {
  color:#999;
}
.header{
	.navUser{
		li{
			padding: 0 0 0 22px;
			a{
				padding: 0;
				svg{
					fill:none;stroke:none;
				}
				&:hover{
					svg{
						#Path_1, #Path_2{
							stroke:$red;
						}
						#account_icon{
							fill:$red;
						}
					}
				}
				&.logout_link{
					margin: 0 0 0 17px;
					svg{
						height: 30px;width: 30px;fill:#333;
					}
					&:hover{
						svg{
							fill:$red;
						}
					}
				}
				&.navUser-item--compare{
					position: relative;padding: 3px 0 0;
					svg{
						fill:$black;
					}
				.countPill{
					position: absolute;right:-9px;bottom: 0;
					margin: 0;height: 18px;width: 18px;background: $red;font-size: 12px;font-family: $font5;line-height: 18px;color: $white;
					&.countPill--alt{
						background-color: $red;
					}
				}
				&:hover{
					svg{
						fill:$red;
					}
					.countPill--alt{
						background-color: $black;
					}
				}
			}
			}
			&.navUser-item--cart{
				a{
					position: relative;
					&:hover{
						.countPill{
							background:$black;
						}
					}
				}
				.countPill{
					position: absolute;right:-9px;bottom: 0;margin: 0;height: 18px;width: 18px;background: $red;font-size: 12px;font-family: $font5;line-height: 18px;
				}
			}

		}
	}
}
.section-category{
	@include row;overflow: hidden;margin: 0 0 100px;
	.section-list{
		padding: 0 14px;margin: 0 -6.5px;
		div{
			padding: 0 6.5px 14px;width: 50%;float: left;
			a{
				display: block;position: relative;text-align: center;
				 @include stickytransition;
				&:before{
					content: "";
				    position: absolute;
				    left: 0px;
				    right: 0px;
				    top: 0px;
				    bottom: 0px;
				    background-color: rgba(0,0,0,0.5);
				    display: block;
				    margin: auto;
				    opacity: 0;
				     @include stickytransition;
				}
				&:hover{
					&:before{
						opacity:1;
					}
					h2{
						margin-top: -31px;
					}
					p{
						margin-top: 62px;opacity: 1;
					}
				}
			}
			img{
				width: 100%;
			}
			h2, p{
				position: absolute;
			    left: 0px;
			    right: 0px;
			    margin: 0px auto auto auto;
			    top: 50%;
			    -moz-transform: translateY(-50%);
			    -webkit-transform: translateY(-50%);
			    -o-transform: translateY(-50%);
			    -ms-transform: translateY(-50%);
			    transform: translateY(-50%);
			    font-weight: normal;
			     @include stickytransition;
			}
			h2{
				font-size: 64px;line-height: 68px;font-family: $font4;color: $white;
			}
			p{
				font-size: 20px;line-height: 22px;letter-spacing: 0.2px;color: #E6E6E6;opacity: 0;
			}
		}
	}
}

.section-featured-artist, .section-collections, .section-newsletter{
	@include row;
}
.section-title{
	@include HP_heading;margin: 0;color: #131527;
}
.section-featured-artist{
	margin: 50px 0 100px;
	.section-title{
		margin: 0 0 40px;
	}
}
.featured-artists{
    margin: 0 -6.5px;
    .brand {
        padding:0px 6.5px;overflow: visible;width:25%;
        .card {
          margin: 0 0 16px;
            .card-figure{
                background: transparent;padding: 0;
                a{
                  display: flex;align-items: center;height:307px;overflow: hidden;justify-content: center;
                .card-img-container{
                    width: 100%; max-width: 100%;margin: auto;
                    img{
                        position: static;max-height:307px;margin: auto;@include transition;
                        &:hover{
                            @include img_hover;@include transition;
                        }
                    }
                    &:after{ 
                        padding: 0; 
                    }
                }
              }
            }
        }
        .card-body{
        	text-align: center;padding: 26px 0 0;
        	.card-title{
        		text-align: center;
        		a{
        			font-size: 20px;line-height: 21px;letter-spacing: 0.2px;font-family: $font1;font-weight: normal;color: #000;text-align: center;
        			&:hover{
        				color:$red;
        			}
        		}
        	}
        }
    }
}

.body .section-featured-artist  .slick-arrow.slick-prev:before,
.body .section-featured-artist  .slick-arrow.slick-prev:after{
	display: none;
}
.body{
	.section-featured-artist, .section-collections{
  .slick-arrow{
         padding: 0;border: 0;top:40%;height:70px;width: 50px;transform: translateY(-40%);border:1px solid $white;text-align: center;background-color: #fff;
         &.slick-next{ 
             right:0;margin: 0;border-radius:50px 0px 0px 50px;left: auto;
             &:before{
                 display:none;
            }
        }
         &.slick-prev {
             left: 0;margin: 0;right:auto;border-radius: 0px 50px 50px 0px;
             &:before{
                 height:15px;width: 12px;background-size: contain;background-image:none;margin: auto;
                  display:none;
            }
        }
    }
    .slick-disabled{
    	opacity: .6;
    }
}
}
// .body .section-featured-artist .slick-arrow.slick-prev{
//     right:5px;
// }
// .body .section-featured-artist .slick-arrow.slick-next{
//     left:9px;
// }
.body .section-collections .slick-arrow.slick-prev, .body .section-collections .slick-arrow.slick-next{
    top:50%;transform: translateY(-50%);
}
.body .section-collections .slick-arrow.slick-prev{
    left: -7px;
}
.body .section-collections .slick-arrow.slick-next{
    right:-7px;
}
.body{
	  .slick-arrow{
	         padding: 0;border: 0;top:50%;height:21px;width: 14px;transform: translateY(-50%);border:0;text-align: center;background-color: transparent;
	         &.slick-next{ 
	             right:0px;margin: 0;
	             &:before{
	                  height:21px;width: 14px;background-size: contain;background-image:none;margin: auto;
	                  background: url("https://wds-sandbox14.mybigcommerce.com/content/slide-right.svg") no-repeat center;
	            }
	        }
	         &.slick-prev {
	             left: 0px;margin: 0;
	             &:before{
	                 height:21px;width: 14px;background-size: contain;background-image:none;margin: auto;
	                  background: url("https://wds-sandbox14.mybigcommerce.com/content/slide-left.svg") no-repeat center;
	            }
	        }
	    }
	    .slick-disabled{
	    	opacity: .6;
	    }
	
}


.body{
  .slick-dots{ 
     bottom: -57px;
     li{
         background: transparent;padding:0; height:6px;width:50px;margin: 0 2.5px;
         &.slick-active{
             button{
                 &:before{
                     background:rgba(0, 0, 0, 1);height:6px;width:50px;left: 0;top:0;

                }
            } 
        }
         button{
             border:0;background: rgba(17, 41, 40, .1);height:6px;width:50px;border-radius: 20px;
             &:before{
                height:6px;width:50px;left:0;top:0;border-radius: 20px;
            }
            &:hover{
              border-color: rgba(0, 0, 0, 1);background: rgba(0, 0, 0, 1);
            } 
        } 
    }
  }
  .carousel-play-pause-button, .carousel-tooltip{
    display: none !important;
  }
}


.section-collections{
	margin: 68px 0 72px;
	.section-title{
		margin: 0 0 40px;
	}
	.slick-list{
		margin: 0 -7px;
		.collection-inner{
			padding: 0 7px;position: relative;
		}
	}
	.collection-inner{
		h2, p, a{
			position: absolute;bottom: 0px;z-index: 2;left: 0px; right: 0px;text-align: center;
			 @include stickytransition;

		}
		h2{
			font-size: 34px;line-height: 68px;color: $white;bottom: 68px;margin:20px auto 10px;
		}
		p{
			font-size: 20px;line-height: 22px;letter-spacing: 0.2px;color: $white;bottom: 100px;opacity: 0;
		}
		a{
			font-size: 21px;line-height: 29px;letter-spacing: -0.21px;color: $white;bottom:49px;
			@include stickytransition;
			&:hover{
				color:$red;
				svg{
					#Path_1545{
						stroke:$red;@include stickytransition;
					}
				}
			}
			svg{
				display: inline-block;margin:-1px 0 0 3px;vertical-align: middle;
			}
		}
		&:before{
			content: "";
		    position: absolute;
		    background-color: #000;
		    left: 7px;
		    top: 0px;
		    bottom: 0px;
		    right: 7px;
		    margin: auto;
		    display: block;
		    opacity: 0;
		    z-index: 1;
		    @include stickytransition;

		}
		&:hover{
			&:before{
				opacity:.7;@include stickytransition;
			}
			h2{
				bottom: 133px;@include stickytransition;
			}
			p{
				bottom: 100px;opacity: 1;@include stickytransition;
			}
		}
	}
}

.section-newsletter{
	margin: 31px 0 0;
	.section-title{
		margin: 0 0 12px;
	}
	.container{
		max-width: 508px;
	}
	p{
		font-size: 18px;line-height: 25px;letter-spacing: 0.18px;color: #999999;margin: 0 0 40px;
	}
	.form{
		margin: 0 0 16px;
		.form-field{
			margin: 0;
		}
	}
	.form-prefixPostfix{
		width: 450px;padding:3px 0;margin: 0 auto;border: 1px solid #CCCCCC;border-radius: 6px;
		.form-input{
			border:0;font-size: 18px;line-height: 25px;color: #ccc;font-weight: normal;
			padding: 10px 19px;
		}
		.button{
			width: 138px;height: 40px;line-height: 38px;padding: 0;text-align: center;margin:1px 3px 0 0;
			font-size: 18px;
			svg{
				margin: 0 0 0 3px;
			}
		}
	}
}

.footer{
	border:0;background: $black;color: $white;padding: 86px 0 31px;
	.footer-info{
    margin:0px;@include row;padding: 0px 0 92px;
    .footer-info-col{
        width:auto;margin: 0 0 23px;padding:0;
